<template>
    <div>
        <div style="display: flex; flex-wrap: nowrap;">
            <el-input v-model="params.name" style="width: 200px;margin-right: 10px" placeholder="请输入操作内容"
                @input="findBySearch()" clearable></el-input>
            <el-select v-model="params.username" placeholder="请选择操作人" style="width: 200px; margin-right: 10px"
                @input="findBySearch()" clearable filterable default-first-option>
                <el-option v-for="item in usernameObjs" :key="item.username" :label="item.username"
                    :value="item.username"></el-option>
            </el-select>
            <el-button type="warning" style="margin-left: 10px" @click="findBySearch()">查询</el-button>
            <el-button type="warning" style="margin-left: 10px" @click="reset()">清空</el-button>
        </div>
        <div>
            <el-table :data="tableData" style="width: 100%; margin: 15px 0px" height="70vh" stripe
                highlight-current-row>
                <el-table-column prop="id" label="序号" fixed width="70" sortable></el-table-column>
                <el-table-column prop="name" label="操作内容"></el-table-column>
                <el-table-column prop="time" label="操作时间"></el-table-column>
                <el-table-column prop="username" label="操作人"></el-table-column>
                <el-table-column prop="ip" label="ip"></el-table-column>
                <el-table-column label="操作" width="160">
                    <template slot-scope="scope">
                        <el-button type="primary" @click="detail(scope.row)">详情</el-button>
                        <el-popconfirm title="确定删除吗？" @confirm="del(scope.row.id)">
                            <el-button slot="reference" type="danger" style="margin-left: 5px">删除</el-button>
                        </el-popconfirm>
                    </template>
                </el-table-column>
            </el-table>
        </div>
        <div style="margin-top: 10px">
            <el-pagination @size-change="handleSizeChange" @current-change="handleCurrentChange"
                :current-page="params.pageNum" :page-sizes="[10, 20, 50, 100]" :page-size="params.pageSize"
                layout="total, sizes, prev, pager, next" :total="total">
            </el-pagination>
        </div>

        <div>
            <el-dialog :visible.sync="dialogFormVisible" width="50%">
                <el-descriptions class="margin-top" title="日志详细信息" :column="2" border>
                    <el-descriptions-item>
                        <template slot="label">
                            <i class="el-icon-collection-tag"></i>
                            日志序号
                        </template>
                        {{ form.id }}
                    </el-descriptions-item>
                    <el-descriptions-item>
                        <template slot="label">
                            <i class="el-icon-document-copy"></i>
                            日志内容
                        </template>
                        {{ form.name }}
                    </el-descriptions-item>

                    <el-descriptions-item>
                        <template slot="label">
                            <i class="el-icon-user"></i>
                            操作人
                        </template>
                        {{ form.username }}
                    </el-descriptions-item>
                    <el-descriptions-item>
                        <template slot="label">
                            <i class="el-icon-timer"></i>
                            操作时间
                        </template>
                        {{ form.time }}
                    </el-descriptions-item>
                    <el-descriptions-item>
                        <template slot="label">
                            <i class="el-icon-location-outline"></i>
                            ip
                        </template>
                        {{ form.ip }}
