<script>
import request from "@/utils/request";

export default {
    data() {
        return {
            params: {
                name: '',
                username: '',
                pageNum: 1,
                pageSize: 20
            },
            tableData: [],
            total: 0,
            dialogFormVisible: false,
            form: {},
            usernameObjs: [],
        }
    },
    // 页面加载的时候，做一些事情，在created里面
    created() {
        this.findBySearch();
    },
    // 定义一些页面上控件出发的事件调用的方法
    methods: {
        findBySearch() {
            request.get("/log/search", {
                params: this.params
            }).then(res => {
                if (res.code === '0') {
                    this.tableData = res.data.list;
                    this.total = res.data.total;
                    this.initUsername();
                } else {
                    this.$message({
                        message: res.msg,
                        type: 'success'
                    });
                }
            })
        },
        reset() {
            this.params = {
                pageNum: 1,
                pageSize: 20,
                name: '',
                username: '',
            }
            this.findBySearch();
        },
        handleSizeChange(pageSize) {
            this.params.pageSize = pageSize;
            this.findBySearch();
        },
        handleCurrentChange(pageNum) {
            this.params.pageNum = pageNum;
            this.findBySearch();
        },
        submit() {
            request.post("/log", this.form).then(res => {
                if (res.code === '0') {
                    this.$message({
                        message: '操作成功',
                        type: 'success'
                    });
                    this.dialogFormVisible = false;
                    this.findBySearch();
                } else {
                    this.$message({
                        message: res.msg,
                        type: 'error'
                    });
                }
            })
        },
        del(id) {
            request.delete("/log/" + id).then(res => {
                if (res.code === '0') {
                    this.$message({
                        message: '删除成功',
                        type: 'success'
                    });
                    this.findBySearch();
                } else {
                    this.$message({
                        message: res.msg,
                        type: 'success'
                    });
                }
            })
        },
        detail(row) {
            this.form = row;
            this.dialogFormVisible = true;
        },
        initUsername() {
            request.get("/log/user").then(res => {
                if (res.code === '0') {
                    this.usernameObjs = res.data;
                } else {
                    this.$message({
                        message: res.msg,
                        type: 'error'
                    });
                }
            })
        },
    }
}
</script>